from typing import Dict, List, Optional
from dataclasses import dataclass, field

@dataclass
class Item:
    id: str
    name: str
    rarity: str
    type: str
    stats: Dict[str, int] = field(default_factory=dict)
    lore: List[str] = field(default_factory=list)
    special_ability: Optional[str] = None
    craft_recipe: Optional[Dict[str, int]] = None
    npc_sell_price: int = 0
    collection_req: Optional[Dict[str, int]] = None
    default_bazaar_price: float = 100

RARITIES = ['COMMON', 'UNCOMMON', 'RARE', 'EPIC', 'LEGENDARY', 'MYTHIC', 'DIVINE', 'SPECIAL', 'VERY SPECIAL', 'ADMIN']

RARITY_COLORS = {
    'COMMON': 'â¬œ', 'UNCOMMON': 'ðŸŸ©', 'RARE': 'ðŸŸ¦', 'EPIC': 'ðŸŸª',
    'LEGENDARY': 'ðŸŸ§', 'MYTHIC': 'ðŸŸ¥', 'DIVINE': 'ðŸŒŸ',
    'SPECIAL': 'ðŸ”´', 'VERY SPECIAL': 'â­', 'ADMIN': 'âš«'
}

RARITY_MULTIPLIERS = {
    'COMMON': 1,
    'UNCOMMON': 5,
    'RARE': 25,
    'EPIC': 125,
    'LEGENDARY': 625,
    'MYTHIC': 3125,
    'DIVINE': 15625,
    'SPECIAL': 10000,
    'VERY SPECIAL': 50000,
    'ADMIN': 1000000
}

TYPE_BASE_PRICES = {
    'ITEM': 10,
    'SWORD': 500,
    'AXE': 400,
    'BOW': 450,
    'HELMET': 300,
    'CHESTPLATE': 500,
    'LEGGINGS': 400,
    'BOOTS': 250,
    'PET': 5000,
    'FISHING_ROD': 350,
    'RUNE': 1000,
    'POTION': 200,
    'TALISMAN': 500,
    'PICKAXE': 300,
    'HOE': 250,
    'ACCESSORY': 200,
    'MINION': 1000,
    'PET_ITEM': 5000
}

ALL_ITEMS: Dict[str, Item] = {}

def calculate_default_bazaar_price(rarity: str, item_type: str) -> float:
    base = TYPE_BASE_PRICES.get(item_type, 100)
    multiplier = RARITY_MULTIPLIERS.get(rarity, 1)
    return float(base * multiplier)

def register_item(item: Item):
    if item.default_bazaar_price == 100:
        item.default_bazaar_price = calculate_default_bazaar_price(item.rarity, item.type)
    ALL_ITEMS[item.id] = item

