def get_item(item_id: str) -> Optional[Item]:
    return ALL_ITEMS.get(item_id)
    return ALL_ITEMS.get(item_id)

def get_items_by_type(item_type: str) -> List[Item]:
    return [item for item in ALL_ITEMS.values() if item.type == item_type]

def format_item_display(item: Item, count: int = 1) -> str:
    rarity_emoji = RARITY_COLORS.get(item.rarity, 'â¬œ')
    stats_str = '\n'.join([f'+{value} {stat.title()}' for stat, value in item.stats.items()])
    lore_str = '\n'.join(item.lore) if item.lore else ''
    
    display = f"{rarity_emoji} **{item.name}** {f'x{count}' if count > 1 else ''}\n"
    if stats_str:
        display += f"{stats_str}\n"
    if lore_str:
        display += f"*{lore_str}*\n"
    display += f"**{item.rarity} {item.type}**"
    
    return display
